"use strict";

/*
 * Test Server
 */

var server_debug = require('debug')('server'),
    i = 0, buffer = ['Welcome to AISIS server.'],
    server_socket = null;
function write_buffer() {
  if ((buffer.length > 0) && (server_socket != null)) {
    var payload = buffer.shift();
    server_debug('Server sending ' + payload); 
    server_socket.write(payload);
  }
  setTimeout(write_buffer, 500 + Math.random()*2000*0);  
}

require('net').createServer(function (socket) {
    server_socket = socket;

    server_debug('New connection to the server from ' + 
        socket.remoteAddress + ':' + socket.remotePort + ' to ' + 
        socket.localAddress + ':' + socket.localPort);

    socket.on('data', function (data) {
        server_debug('Server Received <' + data.toString() + '>');
        switch (data.toString()) {
            case 'plain\n\0': 
                buffer.push('NO uSSL'); 
                break;
            case '1234': 
                buffer.push('LOGIN OK'); 
                break;
            case '!\0': 
                buffer.push('CMD OK');
                break; 
            case '@00000000:get\0': 
                buffer.push('~00000000:Answer from Nexo'); 
                break;
            case '@00000000:test\0': 
                buffer.push('~00000000:Message passed from client through proxy to nexo'); 
                break;
            case '\0': 
                if (++i > 10) {
                    i = 0;
                    buffer.push('~00000000:A message from Nexo sent on pull'); 
                } else {
                    buffer.push('CMD OK'); 
                }
                break;
            default: 
              buffer.push('CMD OK');
              break;
        }
    });
})

.listen(1024);

write_buffer();

/*
 * Test Client
 */

var client_debug = require('debug')('client');
var settings_file = require('./nexo_cred.json').nexo; // This has to go out out of the moduke
var Nexo = require('./nexo'),
    nexo_client = new Nexo(); //to be replaced by new nexo.Socket above thsis module 

nexo_client.setConfig(settings_file);

nexo_client.connectTo(function connected() {
  client_debug('CONNECTED TO: ' + nexo_client.host + ':' + nexo_client.port);
  nexo_client.poolFrom(readData);
})

function readData() {
  if (nexo_client.buffer && nexo_client.buffer.length) 
    client_debug('RECEIVED DATA: ' + nexo_client.buffer);
  else
    client_debug('NO DATA');

  nexo_client.poolFrom(readData);

}

function disconnect() {
  client_debug('END');

  nexo_client.end();
}

/*
 * Proxy server
 */

var proxy_debug = require('debug')('proxy');

require('net').createServer(function (socket) {
    proxy_debug('New connection to the PROXY from ' + 
        socket.remoteAddress + ':' + socket.remotePort + ' to ' + 
        socket.localAddress + ':' + socket.localPort);

    socket.on('data', function (data) {
        proxy_debug('Proxy ' + socket.remotePort + ' Sending <' + data.toString() + '>');
        nexo_client.writeTo(false, data.toString().replace('\r\n',''), function(){
          proxy_debug('Proxy ' + socket.remotePort + ' Received ' + nexo_client.buffer);
        });
    });
})

.listen(8080);

